
public class Flache {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final double pi = Math.PI;
		int r = 5;
		double erg=0;
		
		erg=(r*r)*pi;
		System.out.println("Die Flache ist  "+erg);
		r=8;
		erg=(r*r)*pi;
		System.out.println("Die Flache ist  "+erg);
		r=15;
		erg=(r*r)*pi;
		System.out.println("Die Flache ist  "+erg);
		r=16;
		erg=(r*r)*pi;
		System.out.println("Die Flache ist  "+erg);
		r=2;
		erg=(r*r)*pi;
		System.out.println("Die Flache ist  "+erg);
		

	}

}
