
public class Quadratgleichung {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double x = 5.0;
		double wert = 0;
		wert = x*x+2*x-35;
		System.out.println("das Ergebnis ist "+wert);
		x=0;
		wert = x*x+2*x-35;
		System.out.println("das Ergebnis ist "+wert);
		x=-2;
		wert = x*x+2*x-35;
		System.out.println("das Ergebnis ist "+wert);
			

	}

}
